    transform-origin: center center;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-20px); }
  }

  @keyframes rotate {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(20deg); }
  }

  .boombox-body {
    position: relative;
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, #444, #333);
    border-radius: 10px;
    box-shadow: inset 0 0 10px rgba(0,0,0,0.5);
  }

  .speaker {
    position: absolute;
    width: 40px;
    height: 80px;
    background: #222;
    border-radius: 5px;
    top: 20px;
  }

  .speaker.left {
    left: 20px;
  }

  .speaker.right {
    right: 20px;
  }

  .tape-deck {
    position: absolute;
    width: 80px;
    height: 40px;
    background: #222;
    border-radius: 5px;
    top: 40px;
    left: 60px;
  }

  .tape-window {
    position: absolute;
    width: 40px;
    height: 20px;
    background: #111;
    border-radius: 3px;
    top: 10px;
    left: 20px;
  }

  .controls {
    position: absolute;
    bottom: 5px;
    left: 10px;
    display: flex;
    gap: 5px;
  }

  .button {
    width: 15px;
    height: 15px;
    background: #444;
    border-radius: 50%;
  }

  .equalizer {
    position: absolute;
    bottom: 10px;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 2px;
    height: 20px;
  }

  .bar {
    width: 4px;
    background: #00ff00;
    animation: equalize 1s ease-in-out infinite;
  }

  .bar:nth-child(1) { animation-delay: 0.1s; }
  .bar:nth-child(2) { animation-delay: 0.2s; }
  .bar:nth-child(3) { animation-delay: 0.3s; }
  .bar:nth-child(4) { animation-delay: 0.4s; }

  @keyframes equalize {
    0%, 100% { height: 5px; }
    50% { height: 20px; }
  }

  .handle {
    position: absolute;
    top: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 15px;
    background: #444;
    border-radius: 5px;
  }
`;
